        "name": "<font color='#FFBB63'>블러드 이럽션</font>",
        "slotData": {
          "iconGrade": 10,
          "iconPath": "https://cdn-lostark.game.onstove.com/efui_iconatlas/tripod_tier/tripod_tier_3_13.png",
          "imagePath": ""
        }
      }
    }
  },
